mapper.select:
SELECT EMAIL, FIRST_NAME, LAST_NAME, FULL_NAME, PASSWORD, PROVIDER, ROLE,
-- DATE → 문자열로 변환 TO_CHAR(CREATED_AT, 'YYYY-MM-DD HH24:MI:SS') AS CREATED_AT, LOGIN_FAIL_COUNT, TO_CHAR(LOCK_UNTIL, 'YYYY-MM-DD HH24:MI:SS') AS LOCK_UNTIL, RESET_TOKEN, TO_CHAR(TOKEN_EXPIRE_AT, 'YYYY-MM-DD HH24:MI:SS') AS TOKEN_EXPIRE_AT, ACCOUNT_STATUS, TO_CHAR(SUSPEND_UNTIL, 'YYYY-MM-DD HH24:MI:SS') AS SUSPEND_UNTIL, REFRESH_TOKEN, IS_SUSPENDED, SUSPEND_REASON
FROM USER_INFO WHERE EMAIL = #{email}

mapper.update:
UPDATE USER_INFO
SET LOGIN_FAIL_COUNT = #{count}
WHERE EMAIL = #{email}

UPDATE USER_INFO
SET REFRESH_TOKEN = #{refreshToken}
WHERE EMAIL = #{email}

UPDATE USER_INFO
SET REFRESH_TOKEN = NULL
WHERE EMAIL = #{email}

UPDATE USER_INFO
SET 
    FULL_NAME = #{fullName}

        , PASSWORD = #{password}

WHERE EMAIL = #{email}

UPDATE USER_FAVORITE_NEWS
SET IS_READ = 'Y'
WHERE EMAIL = #{email} 
  AND NEWS_ID = #{newsId}

UPDATE USER_FAVORITE_STOCK
SET MEMO = #{memo}
WHERE EMAIL = #{email} AND STOCK_CODE = #{stockCode}

UPDATE USER_FAVORITE_NEWS
SET MEMO = #{memo}
WHERE EMAIL = #{email} AND NEWS_ID = #{newsId}
